package alipay_test

import (
	"fmt"
	"github.com/smartwalle/alipay"
	"os"
)

var (
	appID = "2016073100129537"

	// RSA2(SHA256)
	aliPublicKey = ""

	privateKey = ""
)

var client *alipay.Client

func init() {
	var err error
	client, err = alipay.New(appID, aliPublicKey, privateKey, false)

	if err != nil {
		fmt.Println("初始化支付宝失败, 错误信息为", err)
		os.Exit(-1)
	}
}
